('562', 'Restaurant L\'Escale', 'Promenade du Barrage', 71700, 'La Truchère', 71, '46,51498', '4,94975', '03 85 51 23 00', NULL, NULL, 'Restaurant situé en bord de Seille.', 'Ouvert du 02/03 au 30/11.', 1),
('563', 'Restaurant L\'Escale', 'Route de Chassey-le-Camp', 71150, 'Remigny', 71, '46,907165', '4,7224865', '03 85 87 07 03', 'mailto: lescaledubois@wanadoo.fr', 'http://www.restaurant-lescale-remigny.com', 'Petit restaurant d\'une capacité de 45 couverts, situé en bordure du Canal du Centre. Cuisine traditionnelle et soignée. Assiette "Tables de Pays" et son verre de Bouzeron servie midi et soir, tous les jours sauf le week-end et jours fériés.', NULL, 1),
('564', 'Restaurant Lesclette', 'Rue Lamartine - Marano Equo', 71800, 'La Clayette', 71, '46,29151234', '4,30838816', '03 85 28 06 53', 'mailto: restaurant.lesclette@wanadoo.fr', 'http://www.restaurant-lesclette.fr', 'Cuisine gastronomique du terroir.', NULL, 1),
